import React from 'react';
import NavBar from './NavBar';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';
import Paques from './images/Paques.jpg';
import Yoshilogo from './images/Yoshilogo.png';
import Logo from './images/Logo.png';
import Background from './images/Background.jpg';

const styles = theme => ({
    root: {
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'space-around',
        overflow: 'hidden',
        backgroundColor: 'transparent',
        marginTop: '5%',
    },
    gridList: {
        paddingTop: '3%',
        width: 700,
        height: 650,
        backgroundColor: 'black',
        opacity: 0.7,

    },
    mainTitle: {
        color: 'rgb(173, 173, 173)',
        fontFamily: 'Abel',
        marginLeft: '35%',
        fontSize: '250%',
    }
});

const tileData = [
    {
        img: Logo,
        title: 'PokeReact',
        description: "Jeu réalisé avec la technologie 'React' en 5 semaines",
        url: 'https://wildcodeschool.github.io/lyon-0219-react-PokeReact/#/',             
    },
    {
        img: Paques,
        title: 'EggsPlosive',
        description: "Hackaton : Jeu réalisé avec la technologie 'React' en 24h",
    },
    {
        img: Background,
        title: 'Portfolio',
        description: "Mon Portfolio réalisé avec la technologie 'React'",
    },
    {
        img: Yoshilogo,
        title: 'Yoshi Save The Baby',
        description: 'Premier projet : Site statique de vente en ligne',
    },
];



function Works(props) {
    const { classes } = props;
    return (
        <div>
            <NavBar />            
            <div className={classes.root}>
                <GridList cellHeight={180} className={classes.gridList}>            
                <GridListTile key="Subheader" cols={2} style={{ height: 'auto' }}>
                    <h1 className={classes.mainTitle}>Réalisations</h1>
                    </GridListTile>
                    {tileData.map(tile => (
                        <GridListTile key={tile.img}>
                            <img src={tile.img} alt={tile.title} component="button" onClick={() => (tile.url)}/>
                            <GridListTileBar
                                title={tile.title}
                                subtitle={<span>{tile.description}</span>}                               
                            />
                        </GridListTile>
                    ))}
                </GridList>
            </div>
        </div>
    );
}

Works.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Works);
