import React from 'react';
import Button from '@material-ui/core/Button';
import { NavLink } from "react-router-dom";
import { Link, DirectLink, Element, Events, animateScroll as scroll, scrollSpy, scroller } from 'react-scroll';
import './App.css';


class Buttons extends React.Component {
    constructor(props) {
      super(props);
      this.scrollToTop = this.scrollToTop.bind(this);
    }

    componentDidMount() {  
      Events.scrollEvent.register('begin', function () {
        console.log("begin", arguments);
      });  
      Events.scrollEvent.register('end', function () {
        console.log("end", arguments);
      });  
    }

    scrollToTop() {
      scroll.scrollToTop();
    }

    scrollTo() {
      scroller.scrollTo('scroll-to-element', {
        duration: 800,
        delay: 0,
        smooth: 'easeInOutQuart'
      })
    }

    scrollToWithContainer() {  
      let goToContainer = new Promise((resolve, reject) => {  
        Events.scrollEvent.register('end', () => {
          resolve();
          Events.scrollEvent.remove('end');
        });

        scroller.scrollTo('scroll-container', {
          duration: 800,
          delay: 0,
          smooth: 'easeInOutQuart'
        });

      });

      goToContainer.then(() =>
        scroller.scrollTo('scroll-container-second-element', {
          duration: 800,
          delay: 0,
          smooth: 'easeInOutQuart',
          containerId: 'scroll-container'
        }));
    }
    componentWillUnmount() {
      Events.scrollEvent.remove('begin');
      Events.scrollEvent.remove('end');
    }
    render() { 
  return (
    <div className='Buttons'>        
                  <Link activeClass="active" className="test1" to="test1" spy={true} smooth={true} duration={500} > <Button >
          Sommaire
        </Button></Link>
                  <Link activeClass="active" className="test2" to="test2" spy={true} smooth={true} duration={500}><Button >
          Bio
        </Button></Link>
                  <Link activeClass="active" className="test3" to="test3" spy={true} smooth={true} duration={500} ><Button disabled >
          Compétences
        </Button> </Link>
                  <Link activeClass="active" className="test4" to="test4" spy={true} smooth={true} duration={500}><Button href="#text-buttons" >
          Réalisations
        </Button></Link>

          <Element name="test1" className="element" >
            test 1
        </Element>

          <Element name="test2" className="element">
            test 2
        </Element>

          <Element name="test3" className="element">
            test 3
        </Element>

          <Element name="test4" className="element">
            test 4
        </Element>

          <Element name="test5" className="element">
            test 5
        </Element>           <a onClick={this.scrollToTop}>To the top!</a>
      </div>
    /*
      <Link activeClass="active" to="/Summary" spy={true} smooth={true} duration={250} containerId="containerElement" style={{ display: 'inline-block', margin: '20px' }}>

        </Link>       
      <NavLink to="/History" activeClassName="hurray">

      </NavLink>
      <NavLink to="/Skills" activeClassName="hurray">

      </NavLink>
      <NavLink to="/Works" activeClassName="hurray">

      </NavLink>
    </div>*/)
}
}

export default Buttons;
