import React, { Component } from 'react';
import { NavLink } from "react-router-dom";
import './App.css';


class Buttons extends Component {
  constructor(props) {
    super(props);
    this.state = { 
      active : false,
    }
  }

  handleClick = () => {
      this.setState({
        active : true,
      })
  }

  render() { 

    return ( 
      <div className='Buttons' >
      <NavLink to="/" activeClassName="hurray" >
        <button className={this.props.CurrentPage==="Cover"? 'grey' : '' } >
          Accueil
        </button>
      </NavLink>
      <NavLink to="/History" activeClassName="hurray">
        <button onClick={this.handleClick} >
          Bio
        </button>
      </NavLink>
      <NavLink to="/Skills" activeClassName="hurray">
        <button onClick={this.handleClick}>
          Compétences
        </button> 
      </NavLink>
      <NavLink to="/Works" activeClassName="hurray">
        <button onClick={this.handleClick}>
          Réalisations
        </button>
      </NavLink>
    </div>
     );
  }
}

export default Buttons;
