import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
import { NavLink } from "react-router-dom";
import PropTypes from 'prop-types';
import './App.css';


class Buttons extends Component {
  constructor(props) {
    super(props);
    this.state = { 
      active : false,
      disabled : false,
     }
  }

  handleClick = () => {
      this.setState({
        active : true,
        disabled : true,
      })
  }

  render() { 
    const { classes } = this.props;
    return ( 
      <div className='Buttons'>
      <NavLink to="/" activeClassName="hurray">
        <Button onClick={this.handleClick} >
          Accueil
        </Button>
      </NavLink>
      <NavLink to="/History" activeClassName="hurray">
        <Button onClick={this.handleClick} >
          Bio
        </Button>
      </NavLink>
      <NavLink to="/Skills" activeClassName="hurray">
        <Button onClick={this.handleClick} >
          Compétences
        </Button> 
      </NavLink>
      <NavLink to="/Works" activeClassName="hurray">
        <Button onClick={this.handleClick} >
          Réalisations
        </Button>
      </NavLink>
    </div>
     );
  }
}

Buttons.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default Buttons;
