import React, { Component } from 'react';
import { NavLink } from "react-router-dom";
import './App.css';


class Buttons extends Component {
  constructor(props) {
    super(props);
    this.state = { 
      active : false,
    }
  }

  handleClick = () => {
      this.setState({
        active : true,
      })
      console.log(this.state.active)
  }

  render() { 

    return ( 
      <div className='Buttons' >
      <NavLink to="/" activeClassName="hurray">
        <button style={{ color: '#444', 
  textDecoration: 'underline'}} onClick={() => {this.handleClick()}} className={this.props.CurrentPage==="Cover"? 'grey' : '' } >
          Accueil
        </button>
      </NavLink>
      <NavLink to="/History" activeClassName="hurray">
        <button className={this.props.CurrentPage==="History"? 'grey' : '' }>
          Bio
        </button>
      </NavLink>
      <NavLink to="/Skills" activeClassName="hurray">
        <button className={this.props.CurrentPage==="Skills"? 'grey' : '' }>
          Compétences
        </button> 
      </NavLink>
      <NavLink to="/Works" activeClassName="hurray">
        <button className={this.props.CurrentPage==="Works"? 'grey' : '' }>
          Réalisations
        </button>
      </NavLink>
    </div>
     );
  }
}

export default Buttons;
