import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import NavLink from "react-router-dom";

import './App.css';




class NavBar extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            isActive: false,
         }
    }

    handleClick = () => {
        this.setState({
          isActive: true,
        })
      }

    render() { 
     const {classes} = props;
    return (
        <div  className={classes.root} >
            <AppBar position="static" color="default" className='NavBar'> 
                <Toolbar >
                <NavLink to="/" activeClassName="hurray">
          <button
            style={{
              color: isActive ? '#444' : 'red',
              textDecoration: 'underline',
          }}
          onClick={() => { this.handleClick() }} >
          Accueil
        </button>
      </NavLink>
      <NavLink to="/History" activeClassName="hurray">
        <button className={this.props.CurrentPage === "History" ? 'grey' : ''}>
          Bio
        </button>
      </NavLink>
      <NavLink to="/Skills" activeClassName="hurray">
        <button className={this.props.CurrentPage === "Skills" ? 'grey' : ''}>
          Compétences
        </button>
      </NavLink>
      <NavLink to="/Works" activeClassName="hurray">
        <button className={this.props.CurrentPage === "Works" ? 'grey' : ''}>
          Réalisations
        </button>
      </NavLink>                                                       
                </Toolbar>
            </AppBar>
        </div>
    );
}
}

export default NavBar;
