import React, { Component } from 'react';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import { NavLink } from "react-router-dom";
import './App.css';

class NavBar extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isActive: true,
    }
  }

  handleClick = () => {
    this.setState({
      isActive: false,
    })
    console.log(this.state)
  }

  render() {
    const { isActive } = this.state;
    return (
      <div>
        <AppBar classes={{ backgroundColor: 'transparent' }}  position="static" color="default"  >
          <Toolbar className='navBar'>
            <NavLink to="/" >
              <button style={{
                color: isActive ? '#444' : 'red',
                textDecoration: 'underline',
              }}
                onClick={() => { this.handleClick() }} >
                Accueil
              </button>
            </NavLink>
            <NavLink to="/History">
              <button style={{
                color: isActive ? '#444' : 'red',
                textDecoration: 'underline',
              }}
                onClick={() => { this.handleClick() }} >
                Bio
        </button>
            </NavLink>
            <NavLink to="/Skills">
              <button style={{
                color: isActive ? '#444' : 'red',
                textDecoration: 'underline',
              }}
                onClick={() => { this.handleClick() }} >
                Compétences
        </button>
            </NavLink>
            <NavLink to="/Works">
              <button style={{
                color: isActive ? '#444' : 'red',
                textDecoration: 'underline',
              }}
                onClick={() => { this.handleClick() }} >
                Réalisations
        </button>
            </NavLink>
          </Toolbar>
        </AppBar>
      </div>
    );
  }
}

export default NavBar;
